    private void visibility(){
        validacion.setVisible(false);
        CampoN1.setText("0");
        CampoN2.setText("0");
        Resultado.setText("0");
    }
    private void btnSumaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSumaActionPerformed
        try{  
            double n1 = Double.parseDouble(CampoN1.getText());
            double n2 = Double.parseDouble(CampoN2.getText());
            if(n1<0 || n2<0){
                validacion.setText("Error, debes ingresar un valor mayor que 0");
                validacion.setVisible(true);
                Resultado.setText("0");
            }else{
                validacion.setVisible(false);
                oper.Multiplicacion(n1, n2);
                Resultado.setText(String.valueOf((int)oper.resultado));
            }
        }catch(NumberFormatException e){
            validacion.setText("Error, debes ingresar un nÃºmero");
            validacion.setVisible(true);
            Resultado.setText("0");
        } 
    }//GEN-LAST:event_btnSumaActionPerformed

